import React from "react";

const styles = {
  cardStyles: {
    margin: "10px 0"
  }
};

function Card(props) {
  return (
    <div className="card" style={styles.cardStyles}>
      <div className="card-header" style={{ background: props.bgColor }}>{props.title}</div>
      <div className="card-body">
        <p className="card-text">
          {props.children}
        </p>
      </div>
    </div>
  );
}

export default Card;
